Ext.define('sion.salary.accounts.view.Payroll', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.grid.Panel',
        'Ext.selection.CheckboxModel',
        'Ext.grid.column.Date',
        'Ext.grid.View',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    height: 600,
    width: 700,
    layout: 'fit',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'gridpanel',
                    store: 'SalaryPlanPayroll',
                    selModel: Ext.create('Ext.selection.CheckboxModel', {

                    }),
                    columns: [
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'subject',
                            text: '薪资主题',
                            flex: 2
                        },
                        {
                            xtype: 'gridcolumn',
                            renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                                return value=='Unpublish'?'待发':'已发';
                            },
                            dataIndex: 'state',
                            text: '状态',
                            flex: 0.7
                        },
                        {
                            xtype: 'datecolumn',
                            dataIndex: 'month',
                            text: '薪资月份',
                            flex: 1,
                            format: 'Y-m-d'
                        },
                        {
                            xtype: 'datecolumn',
                            dataIndex: 'socialCostMonth',
                            text: '社保扣费月',
                            flex: 1,
                            format: 'Y-m-d'
                        },
                        {
                            xtype: 'gridcolumn',
                            text: '发放人数',
                            flex: 1
                        },
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'createPersonName',
                            text: '创建人',
                            flex: 1
                        },
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'createDate',
                            text: '创建日期',
                            flex: 1
                        }
                    ],
                    dockedItems: [
                        {
                            xtype: 'toolbar',
                            dock: 'top',
                            items: [
                                {
                                    xtype: 'button',
                                    text: '更新工资条',
                                    listeners: {
                                        click: {
                                            fn: me.onButtonClick,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        }
                    ]
                }
            ],
            listeners: {
                afterrender: {
                    fn: me.onWindowAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onButtonClick: function(button, e, eOpts) {
        var me = this,
            grid = me.down('gridpanel'),
            ids = [],
            persons = grid.getSelectionModel().getSelection();

        Ext.Array.each(persons, function(item){
            ids.push(item.data.id);
        });
        if(ids.length > 0 ){
            Ext.Ajax.request({
                url :'salary/payroll/generatePayrollItem',//请求的服务器地址
                params : {
                    ids : ids
                },//发送json对象
                success:function(response,action){
                    me.close();
                    Ext.Msg.alert("提示", "更新成功");
                },failure: function(){
                    me.close();
                    Ext.Msg.alert("提示", "更新失败");
                }
            });
        }else{
            me.close();
        }


    },

    onWindowAfterRender: function(component, eOpts) {
        var me = this,
            grid = me.down('grid'),
            store = grid.getStore(),
            accountId = me._accountId;

        store.load({
            params : {
                accountId : accountId
            }
        });


    }

});
