Ext.define('sion.salary.accounts.view.SalaryPlan', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.form.Panel',
        'Ext.form.field.Checkbox',
        'Ext.form.field.TextArea',
        'Ext.grid.Panel',
        'Ext.grid.RowNumberer',
        'Ext.grid.column.Boolean',
        'Ext.grid.column.Action',
        'Ext.grid.View'
    ],

    height: 600,
    width: 800,
    title: '方案信息',

    layout: {
        type: 'vbox',
        align: 'stretch'
    },

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'top',
                    items: [
                        {
                            xtype: 'button',
                            width: 70,
                            iconCls: 's_icon_table_save',
                            text: '保存',
                            listeners: {
                                click: {
                                    fn: me.onButtonClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ],
            items: [
                {
                    xtype: 'form',
                    bodyPadding: 10,
                    header: false,
                    title: 'My Form',
                    items: [
                        {
                            xtype: 'textfield',
                            anchor: '100%',
                            itemId: 'name',
                            fieldLabel: '方案名称',
                            name: 'name'
                        },
                        {
                            xtype: 'checkboxfield',
                            anchor: '100%',
                            fieldLabel: '&nbsp;',
                            labelSeparator: ' ',
                            name: 'enableLevel',
                            boxLabel: '启用薪资体系'
                        },
                        {
                            xtype: 'textareafield',
                            anchor: '100%',
                            fieldLabel: '备注',
                            name: 'remark'
                        }
                    ],
                    dockedItems: [
                        {
                            xtype: 'toolbar',
                            dock: 'bottom',
                            items: [
                                {
                                    xtype: 'button',
                                    iconCls: 's_icon_action_add',
                                    text: '新增',
                                    listeners: {
                                        click: {
                                            fn: me.onAddSalaryItemClick,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        }
                    ]
                },
                {
                    xtype: 'gridpanel',
                    flex: 1,
                    height: 450,
                    itemId: 'itemGrid',
                    header: false,
                    title: '方案项目 ',
                    store: 'AccountItem',
                    columns: [
                        {
                            xtype: 'rownumberer',
                            text: '序号',
                            flex: 1
                        },
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'name',
                            text: '项目名称',
                            flex: 3
                        },
                        {
                            xtype: 'gridcolumn',
                            renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                                var store = Ext.getStore("AccountItemType"),
                                    rec = store.findRecord('id', value);

                                return rec.get("name");
                            },
                            dataIndex: 'type',
                            text: '项目类型',
                            flex: 2
                        },
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'fieldName',
                            text: '字段',
                            flex: 1
                        },
                        {
                            xtype: 'gridcolumn',
                            dataIndex: 'value',
                            text: '公式',
                            flex: 6
                        },
                        {
                            xtype: 'booleancolumn',
                            dataIndex: 'show',
                            text: '是否显示',
                            flex: 2,
                            falseText: '否',
                            trueText: '是'
                        },
                        {
                            xtype: 'actioncolumn',
                            dataIndex: 'date',
                            text: '操作',
                            flex: 2,
                            items: [
                                {
                                    handler: function(view, rowIndex, colIndex, item, e, record, row) {
                                        var panel = this.up('panel').up('panel');

                                        panel.detail(record);
                                    },
                                    iconCls: 's_icon_page_edit',
                                    tooltip: '修改'
                                },
                                {
                                    handler: function(view, rowIndex, colIndex, item, e, record, row) {
                                        var store = Ext.getStore('AccountItem');

                                        Ext.Msg.confirm('提示', '确定要删除吗？', function(text){
                                            if (text == 'yes'){
                                                store.remove(record);
                                            }
                                        });
                                    },
                                    iconCls: 's_icon_cross',
                                    tooltip: '删除'
                                }
                            ]
                        }
                    ],
                    listeners: {
                        itemdblclick: {
                            fn: me.onItemGridItemDblClick,
                            scope: me
                        }
                    }
                }
            ],
            listeners: {
                render: {
                    fn: me.onWindowRender,
                    scope: me
                },
                beforerender: {
                    fn: me.onWindowBeforeRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onButtonClick: function(button, e, eOpts) {
        var me = this,
            namespace = me.getNamespace(),
            form = me.down("form"),
            store = Ext.getStore("Account"),
            itemGrid = me.down('#itemGrid'),
            itemList = [],
            itemStore = itemGrid.getStore();

        record = form.getRecord();
        form.updateRecord(record);
        if(!me.down('#name').isValid()){
            Ext.Msg.alert("提示", "信息不完整，请继续填写！");
            return false;
        }
        itemStore.each(function(item){
            itemList.push(item.data);
        });
        record.set('accountItems', itemList);
        record.set('persons', null);
        if(record.get('id') === ''){
            store.add(record);
        }
        store.sync({
            success: function(response, opts){
                Ext.Msg.alert("提示", "保存成功");
                store.load();
                me.close();
            },
            failure: function(){
                Ext.Msg.alert("提示", "保存失败");
                me.close();
            }
        });
    },

    onAddSalaryItemClick: function(button, e, eOpts) {
        this.detail(null);
    },

    onItemGridItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.detail(record);
    },

    onWindowRender: function(component, eOpts) {
        // var me = this,
        //     namespace = me.getNamespace(),
        //     itemStore = Ext.getStore('AccountItem'),
        //     form = me.down("form"),
        //     account = me._account;

        // itemStore.removeAll();
        // if(account){
        //     itemStore.add(account.get('accountItems'));
        //     form.loadRecord(account);
        // }else{
        //     form.loadRecord(Ext.create(namespace + '.model.Account', {
        //         id: '',
        //         name: ''
        //     }));
        // }
    },

    onWindowBeforeRender: function(component, eOpts) {
        var me = this,
            namespace = me.getNamespace(),
            itemStore = Ext.getStore('AccountItem'),
            form = me.down("form"),
            account = me._account;

        itemStore.removeAll();
        if(account){
            itemStore.add(account.get('accountItems'));
            form.loadRecord(account);
        }else{
            form.loadRecord(Ext.create(namespace + '.model.Account', {
                id: '',
                name: ''
            }));
        }
    },

    detail: function(record) {
        var me = this,
            grid = me.down('grid'),
            namespace = me.getNamespace();
        //     accountItem = Ext.create(namespace + '.model.AccountItem');

        Ext.create(namespace+".view.AddSalaryItem", {
            //     _account : me._account,
            _accountItem : record,
            _store : grid.getStore()
        }).show();
    }

});